import React, { useState } from 'react';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../app/rootReducer';  // Replace with your actual root reducer path
import { updateField, resetForm } from '../features/contactSlice';  // Replace with your actual slice path

const Contact: React.FC = () => {
  const dispatch = useDispatch();
  const { name, email, message } = useSelector((state: RootState) => state.contact);  // Replace `state.contact` with your actual state slice

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    dispatch(updateField({ field: name as keyof typeof name, value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/api/sendEmail', { name, email, message });
      if (response.data === 'Email sent') {
        alert('Email sent successfully!');
        dispatch(resetForm());
      }
    } catch (error) {
      alert('Failed to send email. Please try again later.');
    }
  };

  return (
    <div>
      <h1>Contact Me</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name</label>
          <input type="text" id="name" name="name" value={name} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="email">Email</label>
          <input type="email" id="email" name="email" value={email} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="message">Message</label>
          <textarea id="message" name="message" value={message} onChange={handleChange} required></textarea>
        </div>
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default Contact;
