// Contact.tsx
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateField } from '../features/contactSlice';
import { RootState } from './store';

const Contact: React.FC = () => {
  const dispatch = useDispatch();
  const { name, email, message } = useSelector((state: RootState) => state.contact);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    dispatch(updateField({ field: name as keyof ContactState, value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Here you would send the form data to your server
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Your form fields, which use `name`, `email`, and `message` from Redux state */}
    </form>
  );
};

export default Contact;
