// Contact.tsx
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Form, Button, Container } from 'react-bootstrap';
import { updateField } from '../features/contactSlice'; // Update the import path based on your folder structure
import { RootState } from '../store'; // Update the import path based on your folder structure

const Contact: React.FC = () => {
  const dispatch = useDispatch();
  const { name, email, message } = useSelector((state: RootState) => state.contact);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    dispatch(updateField({ field: name as keyof typeof message, value }));
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    // Implement your email sending logic here, usually by sending data to your server
    console.log("Form submitted", { name, email, message });
  };

  return (
    <Container>
      <h1>Contact Me</h1>
      <p>If you have any questions or would like to discuss an opportunity, feel free to reach out.</p>
      
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formName">
          <Form.Label>Name</Form.Label>
          <Form.Control 
            type="text" 
            placeholder="Enter your name" 
            name="name" 
            value={name} 
            onChange={handleChange}
          />
        </Form.Group>
        
        <Form.Group controlId="formEmail">
          <Form.Label>Email</Form.Label>
          <Form.Control 
            type="email" 
            placeholder="Enter your email" 
            name="email" 
            value={email} 
            onChange={handleChange} 
          />
        </Form.Group>

        <Form.Group controlId="formMessage">
          <Form.Label>Message</Form.Label>
          <Form.Control 
            as="textarea" 
            rows={3} 
            placeholder="Your message here" 
            name="message" 
            value={message} 
            onChange={handleChange}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>

      <p>Or connect with me on <a href="your-linkedin-profile">LinkedIn</a> and <a href="your-github-profile">GitHub</a>.</p>

      <p>Thank you for visiting my portfolio!</p>
    </Container>
  );
};

export default Contact;
